    private void registerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_registerActionPerformed
try{
    Class.forName("com.mysql.cj.jdbc.Driver");
    Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/Travelmanagement","root","Manish6022@");
   
    String query="insert into Login(firstname,lastname,username,email,contact,password,security,answer) values(?,?,?,?,?,?,?,?)";
    PreparedStatement ps=con.prepareStatement(query);
       
    
    
    ps.setString(1,first.getText());
    ps.setString(2,last.getText());
    ps.setString(3,etusername.getText());
    ps.setString(4,email.getText());
    ps.setString(5,contact.getText());
    ps.setString(6,etpassword.getText());
    ps.setString(7,(String)etsecurity.getSelectedItem());
    ps.setString(8,answer.getText());
    
        JOptionPane.showMessageDialog(this,"record has succefully Stored");
        dispose();
        Login hpage = new Login();
        hpage.show();
        int i = ps.executeUpdate();
        
    
        ps.close();
        con.close();
    
       

}catch(Exception e1){
       
     JOptionPane.showMessageDialog(this,"please fill the all details", "Error", JOptionPane.ERROR_MESSAGE);
}
                                            // TODO add your handling code here:
    }//GEN-LAST:event_registerActionPerformed

